<?php /**  * Smarty plugin  * @package Smarty  * @subpackage plugins  */ /**  * Smarty {implode} function plugin  *  * Type:     function<br>  * Name:     implode<br>  * Purpose:  glue an array together as a string, with spupplied string glue, and assign it to the template<br>  * @link http://smarty.php.net/manual/en/language.function.implode.php {implode}  *       (Smarty online manual)  * @author Will Mason <will at dontblinkdesign dot com>  * @param array  * @param Smarty  */ function smarty_modifier_implode($array, $separator=','){     if (!is_array($array)) {        return $array;    }    return implode($separator, $array);} /* vim: set expandtab: */ ?> 